$u="https://raw.githubusercontent.com/kostya-zero/velocity/main/velocity.ps1";$p=$PROFILE;$d=Split-Path $p -Parent;$null=ni $d -Type Directory -Force -EA 0;$s=Join-Path $d (Split-Path $u -Leaf);$null=irm $u -OutFile $s;if(!(Test-Path $p)){$null=ni $p -Type File -Force};$l=". `"$s`"";if(!(sls -Path $p -Pattern ([regex]::Escape($l)) -Quiet)){$null=ac $p "`n$l"};Write-Host "Velocity has been installed. Please restart PowerShell to apply changes."